#!/bin/bash
sudo sed -i '/\[multilib\]/,/Include/s/^#//' /etc/pacman.conf; sudo pacman -Syu --noconfirm --needed; CPU_VENDOR=$(grep -m1 'vendor_id' /proc/cpuinfo); IS_BROADCOM=$(lspci -n | grep -c "14e4:"); IS_NVIDIA=$(lspci -n | grep -c "10de:"); IS_LAPTOP=$([ -d /sys/class/power_supply/BAT* ] || [ -d /sys/class/input/mouse* ] && echo 1 || echo 0); DRIVERS="sof-firmware alsa-firmware tlp thermald mesa lib32-mesa intel-media-driver"; [[ "$CPU_VENDOR" =~ "GenuineIntel" ]] && DRIVERS+=" intel-ucode vulkan-intel" || DRIVERS+=" amd-ucode"; [[ "$IS_BROADCOM" -gt 0 ]] && DRIVERS+=" broadcom-wl"; [[ "$IS_NVIDIA" -gt 0 ]] && DRIVERS+=" nvidia nvidia-utils nvidia-settings lib32-nvidia-utils"; [[ "$IS_LAPTOP" -eq 1 ]] && DRIVERS+=" xf86-input-libinput"; sudo pacman -S --noconfirm --needed $DRIVERS lxqt-session lxqt-panel lxqt-config lxqt-qtplugin lxqt-powermanagement lxqt-notificationd lxqt-policykit lxqt-globalkeys lxqt-runner lxqt-themes lximage-qt pcmanfm-qt qterminal breeze-icons gvfs falkon neovim fastfetch htop mpv nano bc ncdu git light-locker xorg-server lightdm lightdm-gtk-greeter pipewire-audio pavucontrol-qt alsa-utils bluez bluez-utils blueman networkmanager network-manager-applet xdg-utils picom; sudo sed -i 's/#greeter-session=.*/greeter-session=lightdm-gtk-greeter/' /etc/lightdm/lightdm.conf; sudo systemctl enable lightdm bluetooth.service NetworkManager.service tlp.service thermald.service; mkdir -p ~/.config/pcmanfm-qt/lxqt/ && echo -e "[General]\ntrust_all_desktop_files=true" > ~/.config/pcmanfm-qt/lxqt/settings.conf; mkdir -p ~/.config/autostart; echo -e "[Desktop Entry]\nType=Application\nExec=picom -b\nName=Picom" > ~/.config/autostart/picom.desktop; echo -e "[Desktop Entry]\nType=Application\nExec=nm-applet\nName=Network Manager" > ~/.config/autostart/nm-applet.desktop; echo -e "[Desktop Entry]\nType=Application\nExec=blueman-applet\nName=Bluetooth Manager" > ~/.config/autostart/blueman.desktop; echo -e "[Desktop Entry]\nType=Application\nExec=light-locker\nName=Light Locker" > ~/.config/autostart/light-locker.desktop; sudo chown -R $USER:$USER ~/.config; sudo pacman -Scc --noconfirm; sync; sleep 5; sudo reboot
