#!/bin/bash
pacman -Syu --noconfirm xorg-server lightdm lightdm-gtk-greeter openbox lxqt-session lxqt-panel lxqt-config lxqt-qtplugin lxqt-powermanagement lxqt-notificationd lxqt-policykit pcmanfm-qt qterminal breeze-icons fastfetch htop && pacman -Scc --noconfirm && systemctl enable lightdm && REAL_USER=$(ls /home | head -n 1) && mkdir -p /home/$REAL_USER/.config/pcmanfm-qt/lxqt/ && echo -e "[General]\ntrust_all_desktop_files=true" >> /home/$REAL_USER/.config/pcmanfm-qt/lxqt/settings.conf && chown -R $REAL_USER:$REAL_USER /home/$REAL_USER/.config
