#!/bin/bash
sudo pacman -Syu --noconfirm && sudo pacman -S --noconfirm xorg-server lightdm lightdm-gtk-greeter lxqt-session lxqt-panel lxqt-config lxqt-qtplugin lxqt-powermanagement lxqt-notificationd lxqt-policykit lxqt-globalkeys lxqt-runner lxqt-themes pcmanfm-qt qterminal breeze-icons falkon neovim fastfetch htop openbox lximage-qt mpv featherpad kcalc gvfs xdg-utils picom && sudo systemctl enable lightdm && sudo sed -i 's/#greeter-session=.*/greeter-session=lightdm-gtk-greeter/' /etc/lightdm/lightdm.conf && mkdir -p ~/.config/pcmanfm-qt/lxqt/ && echo -e "[General]\ntrust_all_desktop_files=true" > ~/.config/pcmanfm-qt/lxqt/settings.conf && mkdir -p ~/.config/autostart && echo -e "[Desktop Entry]\nType=Application\nExec=picom -b\nName=Picom\nComment=Graphics Compositor" > ~/.config/autostart/picom.desktop && sudo pacman -Scc --noconfirm && sync && reboot
